mapper.select:
SELECT p.id AS id, p.name AS name, p.price AS price, p.thumbnail_image_url AS imageUrl, p.created_at AS createdAt, p.category AS category, ( SELECT COUNT(*) FROM orders o WHERE o.product_id = p.id ) AS orderCount FROM product p WHERE 1=1
AND p.width <= #{width}
AND p.depth <= #{depth}
AND p.height <= #{height}
ORDER BY p.price ASC
ORDER BY p.price DESC
ORDER BY orderCount DESC
ORDER BY p.created_at DESC
OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT COUNT(*)
FROM product p
WHERE 1=1

    AND p.width <= #{width}


    AND p.depth <= #{depth}


    AND p.height <= #{height}
